<script>
    // Example starter JavaScript for disabling form submissions if there are invalid fields
    (function() {
        'use strict';
        const api = axios.create({
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            baseURL: window.location.origin + "/api"
        });
        const submitListeners = {
            "add-printer-form": function(form) {
                api.post('/printers', {term:form[1].value})
                    .then((reply) => {
                        console.log({reply});
                    })
                    .catch((error) => {
                        console.log({error});
                    })
                    .finally(() => {
                        $('#add-printer-modal').modal('hide');
                        window.location.reload();
                    });
            }
        };
        window.addEventListener('load', function() {
            $('button.btn.btn-dark.btn-sm').click(function(e) {
                const term = e.target.dataset.name;
                api.post('/printers', {term:term})
                    .then((reply) => {
                        let html = '';
                        $('#detailsModalLabel').html(reply.data.name);
                        Array.from(reply.data.parts).forEach((r) => {
                            html += '<tr><td>' + r.name + '</td><td>' +
                                r.type + '</td><td>' +
                                r.color + '</td><td>' +
                                r.yield + '</td><td>' +
                                r.price + '</td></tr>';
                        });
                        $('#printerPartsBody').html(html);
                        $('#detailsModal').modal('show');
                    })
                    .catch((error) => {
                        console.log({error});
                    });
            });
            // Fetch all the forms we want to apply custom Bootstrap validation styles to
            const forms = document.getElementsByClassName('needs-validation');
            // Loop over them and prevent submission
            const validation = Array.prototype.filter.call(forms, function(form) {
                form.addEventListener('submit', function(event) {
                    if (form.checkValidity() === false) {
                        event.preventDefault();
                        event.stopPropagation();
                    } else { // Valid -> check handlers by form id:
                        if(submitListeners[form.id]) {
                            event.preventDefault();
                            submitListeners[form.id](form);
                        }
                    }
                    form.classList.add('was-validated');
                }, false);
            });
        }, false);
    })();
</script>
